import React, { useState } from 'react';
import outpassService from '../Services/outpassService';

function OutpassForm() {
  const [details, setDetails] = useState({
    name: '',
    hostelName: '',
    leavingDate: '',
    leavingTime: '',
    returningDate: '',
    returningTime: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await outpassService.apply(details);
      alert('Outpass applied successfully');
    } catch (err) {
      alert('Failed to apply outpass');
    }
  };

  return (
    <form className="bg-white p-6 rounded shadow-md" onSubmit={handleSubmit}>
      <h3 className="text-xl font-bold mb-4">Apply for Outpass</h3>
      {/* Add input fields for name, hostelName, leavingDate, leavingTime, returningDate, returningTime */}
      {/* Submit button */}
    </form>
  );
}

export default OutpassForm;
