import React, { useState } from 'react';
import axios from 'axios';

const OutpassForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    hostelName: '',
    leavingDate: '',
    leavingTime: '',
    returningDate: '',
    returningTime: ''
  });
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/outpass', formData);
      if (response.status === 201) {
        setSuccessMessage('Outpass submitted successfully!');
        onSubmit(response.data); // Pass submitted data back to the parent
        setFormData({
          name: '',
          hostelName: '',
          leavingDate: '',
          leavingTime: '',
          returningDate: '',
          returningTime: ''
        });
      }
    } catch (error) {
      console.error('Error submitting outpass:', error);
      setSuccessMessage('Failed to submit outpass');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <form className="bg-white p-6 rounded shadow-md" onSubmit={handleSubmit}>
      <h3 className="text-xl font-bold mb-4">Apply for Outpass</h3>
      {successMessage && <div className="text-green-500 mb-4">{successMessage}</div>}
      
      {/* Name Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      {/* Hostel Name Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Hostel Name</label>
        <input
          type="text"
          name="hostelName"
          value={formData.hostelName}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      {/* Leaving Date Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Leaving Date</label>
        <input
          type="date"
          name="leavingDate"
          value={formData.leavingDate}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      {/* Leaving Time Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Leaving Time</label>
        <input
          type="time"
          name="leavingTime"
          value={formData.leavingTime}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      {/* Returning Date Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Returning Date</label>
        <input
          type="date"
          name="returningDate"
          value={formData.returningDate}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      {/* Returning Time Input */}
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Returning Time</label>
        <input
          type="time"
          name="returningTime"
          value={formData.returningTime}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
        Submit
      </button>
    </form>
  );
};

export default OutpassForm;
