import React, { useState, useEffect } from 'react';
import OutpassForm from './OutpassForm';
import api from '../api';

const StudentDashboard = () => {
  const [outpasses, setOutpasses] = useState([]);
  const [userData, setUserData] = useState({
    studentName: '',
    college_id: '',
    hostelName: ''
  });

  useEffect(() => {
    fetchOutpasses();
    fetchUserData();
  }, []);

  const fetchOutpasses = async () => {
    try {
      const response = await api.get('/api/outpasses');
      setOutpasses(response.data);
    } catch (error) {
      console.error('Error fetching outpasses:', error);
    }
  };

  const fetchUserData = async () => {
    try {
      const response = await api.get('/api/user/profile');
      setUserData({
        studentName: response.data.name,
        college_id: response.data.college_id,
        hostelName: response.data.hostelName
      });
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const handleOutpassSubmit = (newOutpass) => {
    setOutpasses([...outpasses, newOutpass]);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-bold mb-6 text-indigo-700">Student Dashboard</h2>
      <OutpassForm onSubmit={handleOutpassSubmit} userData={userData} />
      <div className="mt-8">
        <h3 className="text-2xl font-semibold mb-4">Your Outpasses</h3>
        {outpasses.length === 0 ? (
          <p>No outpasses found.</p>
        ) : (
          <ul>
            {outpasses.map((outpass) => (
              <li key={outpass._id} className="mb-4 p-4 bg-white shadow rounded">
                <p>From: {new Date(outpass.fromDate).toLocaleDateString()}</p>
                <p>To: {new Date(outpass.toDate).toLocaleDateString()}</p>
                <p>Status: {outpass.status}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
