import React, { useState } from 'react';

const OutpassForm = ({ onSubmit }) => {
  // State for form inputs
  const [formData, setFormData] = useState({
    name: '',
    hostelName: '',
    leavingDate: '',
    leavingTime: '',
    returningDate: '',
    returningTime: ''
  });

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    // Optionally reset form after submission
    setFormData({
      name: '',
      hostelName: '',
      leavingDate: '',
      leavingTime: '',
      returningDate: '',
      returningTime: ''
    });
  };

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <form className="bg-white p-6 rounded shadow-md" onSubmit={handleSubmit}>
      <h3 className="text-xl font-bold mb-4">Apply for Outpass</h3>
      
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Name</label>
        <input 
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Hostel Name</label>
        <input 
          type="text"
          name="hostelName"
          value={formData.hostelName}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Leaving Date</label>
        <input 
          type="date"
          name="leavingDate"
          value={formData.leavingDate}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Leaving Time</label>
        <input 
          type="time"
          name="leavingTime"
          value={formData.leavingTime}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Returning Date</label>
        <input 
          type="date"
          name="returningDate"
          value={formData.returningDate}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Returning Time</label>
        <input 
          type="time"
          name="returningTime"
          value={formData.returningTime}
          onChange={handleChange}
          className="border rounded w-full py-2 px-3 text-gray-700"
          required
        />
      </div>

      <button 
        type="submit" 
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Submit
      </button>
    </form>
  );
};

export default OutpassForm;
