import React, { useState, useEffect } from 'react';
import api from '../api';

const WardenDashboard = ({ hostel }) => {
  const [outpasses, setOutpasses] = useState([]);

  useEffect(() => {
    fetchOutpasses();
    const interval = setInterval(fetchOutpasses, 30000);
    return () => clearInterval(interval);
  }, [hostel]);

  const fetchOutpasses = async () => {
    try {
      const response = await api.get(`/api/outpasses?hostel=${hostel}&status=pending`);
      setOutpasses(response.data);
    } catch (error) {
      console.error('Error fetching outpasses:', error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: 'approved' });
      setOutpasses(outpasses.map(outpass => 
        outpass._id === id ? { ...outpass, status: 'approved' } : outpass
      ));
    } catch (error) {
      console.error('Error approving outpass:', error);
    }
  };

  const handleReject = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: 'rejected' });
      setOutpasses(outpasses.map(outpass => 
        outpass._id === id ? { ...outpass, status: 'rejected' } : outpass
      ));
    } catch (error) {
      console.error('Error rejecting outpass:', error);
    }
  };

  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return `${date.toLocaleDateString()} ${date.toLocaleTimeString()}`;
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-bold mb-6 text-indigo-700">Warden Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {outpasses.length === 0 ? (
          <p className="text-lg text-gray-600">No pending outpass requests.</p>
        ) : (
          outpasses.map((outpass) => (
            <div key={outpass._id} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow duration-300">
              <h3 className="text-xl font-semibold mb-2 text-indigo-600">{outpass.studentName}</h3>
              <p className="text-gray-600 mb-1">College ID: {outpass.studentId.college_id}</p>
              <p className="text-gray-600 mb-1">Leave: {formatDateTime(outpass.fromDate)}</p>
              <p className="text-gray-600 mb-3">Return: {formatDateTime(outpass.toDate)}</p>
              <p className="text-gray-700 mb-4">{outpass.reason}</p>
              <div className="flex justify-between">
                <button
                  onClick={() => handleApprove(outpass._id)}
                  className={`${
                    outpass.status === 'approved' ? 'bg-green-600' : 'bg-green-500 hover:bg-green-600'
                  } text-white font-bold py-2 px-4 rounded transition duration-300`}
                  disabled={outpass.status === 'approved' || outpass.status === 'rejected'}
                >
                  {outpass.status === 'approved' ? 'Approved' : 'Approve'}
                </button>
                <button
                  onClick={() => handleReject(outpass._id)}
                  className={`${
                    outpass.status === 'rejected' ? 'bg-red-600' : 'bg-red-500 hover:bg-red-600'
                  } text-white font-bold py-2 px-4 rounded transition duration-300`}
                  disabled={outpass.status === 'approved' || outpass.status === 'rejected'}
                >
                  {outpass.status === 'rejected' ? 'Rejected' : 'Reject'}
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default WardenDashboard;
