import React, { useState, useEffect } from "react";
import api from "../api";
import { getWardenInfo } from "../controllers/WardenController";


const WardenDashboard = () => {
  const [outpasses, setOutpasses] = useState([]);
  // const [wardenHostel, setWardenHostel] = useState(null);
  const [hostelName, setHostelName] = useState(null);

  useEffect(() => {
    const fetchWardenInfo = async () => {
      const info = await getWardenInfo();
      setHostelName(info.hostelName);
    };
    fetchWardenInfo();
  }, []);

  console.log("Warden's hostel:", hostelName);



  useEffect(() => {
    if(hostelName){
      fetchOutpasses();
      const interval = setInterval(fetchOutpasses, 30000); // Refresh every 30 seconds
      return () => clearInterval(interval);
    }
   }, [hostelName]);

  const fetchOutpasses = async () => {
    try {
      const response = await api.get(
        `/api/outpasses?hostel=${hostelName}&status=pending`
      );
      console.log("All outpasses:", response.data);

      // Filter outpasses to only include those matching the warden's hostel
      const filteredOutpasses = response.data.filter(outpass => outpass.hostelName === hostelName);
      console.log("Filtered outpasses:", filteredOutpasses);
      setOutpasses(filteredOutpasses);
      console.log("Filtered outpasses:", filteredOutpasses);

    } catch (error) {
      console.error("Error fetching outpasses:", error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: "approved" });
      setOutpasses(
        outpasses.map((outpass) =>
          outpass._id === id ? { ...outpass, status: "approved" } : outpass
        )
      );
    } catch (error) {
      console.error("Error approving outpass:", error);
    }
  };

  const handleReject = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: "rejected" });
      setOutpasses(
        outpasses.map((outpass) =>
          outpass._id === id ? { ...outpass, status: "rejected" } : outpass
        )
      );
    } catch (error) {
      console.error("Error rejecting outpass:", error);
    }
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return "Not specified";
    const date = new Date(dateTimeString);
    return date instanceof Date && !isNaN(date)
      ? date.toLocaleString("en-US", {
          dateStyle: "medium",
          timeStyle: "short",
        })
      : "Invalid Date";
  };

  const handleRemove = (id) => {
    setOutpasses(outpasses.filter((outpass) => outpass._id !== id));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-4xl font-bold mb-8 text-black">Warden Dashboard</h2>
      {outpasses.length === 0 ? (
        <p className="text-lg text-gray-600">No pending outpass requests.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {outpasses.map((outpass) => (
            <div
              key={outpass._id}
              className="bg-white rounded-lg shadow-lg p-6 hover:shadow-xl transition-shadow duration-300 relative"
            >
              <button
                onClick={() => handleRemove(outpass._id)}
                className="absolute top-2 right-2 text-gray-500 hover:text-red-500 transition-colors duration-300"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>

              <div className="mb-4">
                <h3 className="text-2xl font-semibold mb-2 text-indigo-600">
                  Name: {outpass.name}
                </h3>
                <p className="text-gray-600 mb-1">
                  Hostel: {outpass.hostelName}
                </p>
                <p className="text-gray-600 mb-1">
                  Leaving Date:{" "}
                  {new Date(outpass.leavingDate).toLocaleDateString()}
                </p>
                <p className="text-gray-600 mb-1">
                  Leaving Time: {formatDateTime(outpass.leavingDate)}
                </p>

                <p className="text-gray-600 mb-1">
                  Returning Date:{" "}
                  {new Date(outpass.returningDate).toLocaleDateString()}
                </p>
                <p className="text-gray-600 mb-1">
                  Returning Time: {formatDateTime(outpass.returningDate)}
                </p>

                <p className="text-gray-700 mb-4">{outpass.reason}</p>
              </div>
              <div className="flex justify-between">
                <button
                  onClick={() => handleApprove(outpass._id)}
                  className={`bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-full transition duration-300 ${
                    outpass.status === "approved"
                      ? "opacity-50 cursor-not-allowed"
                      : ""
                  }`}
                  disabled={outpass.status === "approved"}
                >
                  {outpass.status === "approved" ? "Approved" : "Approve"}
                </button>
                <button
                  onClick={() => handleReject(outpass._id)}
                  className={`bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded-full transition duration-300 ${
                    outpass.status === "rejected"
                      ? "opacity-50 cursor-not-allowed"
                      : ""
                  }`}
                  disabled={outpass.status === "rejected"}
                >
                  {outpass.status === "rejected" ? "Rejected" : "Reject"}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WardenDashboard;
