import React, { useState, useEffect } from 'react';
import api from '../api';

const WardenDashboard = ({ hostel }) => {
  const [outpasses, setOutpasses] = useState([]);

  useEffect(() => {
    fetchOutpasses();
    const interval = setInterval(fetchOutpasses, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, [hostel]);

  const fetchOutpasses = async () => {
    try {
      const response = await api.get(`/api/outpasses?hostel=${hostel}&status=pending`);
      setOutpasses(response.data);
    } catch (error) {
      console.error('Error fetching outpasses:', error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: 'approved' });
      fetchOutpasses();
    } catch (error) {
      console.error('Error approving outpass:', error);
    }
  };

  const handleReject = async (id) => {
    try {
      await api.put(`/api/outpass/${id}`, { status: 'rejected' });
      fetchOutpasses();
    } catch (error) {
      console.error('Error rejecting outpass:', error);
    }
  };
//cody
  // return (
  //   <div className="container mx-auto px-4 py-8">
  //     <h2 className="text-3xl font-bold mb-6 text-indigo-700">Warden Dashboard</h2>
  //     <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
  //       {outpasses.length === 0 ? (
  //         <p className="text-lg text-gray-600">No pending outpass requests.</p>
  //       ) : (
  //         outpasses.map((outpass) => (
  //           <div key={outpass._id} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow duration-300">
  //             <h3 className="text-xl font-semibold mb-2 text-indigo-600">{outpass.studentName}</h3>
  //             <p className="text-gray-600 mb-1">From: {new Date(outpass.fromDate).toLocaleDateString()}</p>
  //             <p className="text-gray-600 mb-3">To: {new Date(outpass.toDate).toLocaleDateString()}</p>
  //             <p className="text-gray-700 mb-4">{outpass.reason}</p>
  //             <div className="flex justify-between">
  //               <button
  //                 onClick={() => handleApprove(outpass._id)}
  //                 className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded transition duration-300"
  //               >
  //                 Approve
  //               </button>
  //               <button
  //                 onClick={() => handleReject(outpass._id)}
  //                 className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded transition duration-300"
  //               >
  //                 Reject
  //               </button>
  //             </div>
  //           </div>
  //         ))
  //       )}
  //     </div>
  //   </div>
  // );


  //chatgpt for ui\
  const handleStatusUpdate = async (id, status) => {
    try {
      await axios.put(`/api/outpass/${id}`, { status });
      fetchOutpasses();
    } catch (error) {
      console.error('Error updating outpass status:', error);
    }
  };
  
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center text-purple-700">
        Warden Dashboard
      </h1>
      <h2 className="text-2xl font-semibold mb-6 text-center text-purple-500">
        Pending Outpasses for Hostel: {wardenHostel}
      </h2>
      {outpasses.length === 0 ? (
        <p className="text-center text-lg text-gray-600">
          No pending outpasses for this hostel
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {outpasses.map((outpass) => (
            <div
              key={outpass._id}
              className="bg-white rounded-lg shadow-md p-6 transform transition duration-500 hover:scale-105"
            >
              <div className="mb-4">
                <h3 className="text-xl font-bold text-purple-700">
                  {outpass.studentName}
                </h3>
                <p className="text-gray-600">Roll No: {outpass.rollNo}</p>
                <p className="text-gray-600">Hostel: {outpass.hostelName}</p>
                <p className="text-gray-600">Reason: {outpass.reason}</p>
                <p className="text-gray-600">
                  From: {new Date(outpass.fromDate).toLocaleDateString()}
                </p>
                <p className="text-gray-600">
                  To: {new Date(outpass.toDate).toLocaleDateString()}
                </p>
                <p className="text-gray-600">
                  Status: <span className="font-semibold">{outpass.status}</span>
                </p>
              </div>
              <div className="flex justify-between">
                <button
                  onClick={() => handleStatusUpdate(outpass._id, 'approved')}
                  className="bg-green-500 text-white px-4 py-2 rounded-md shadow hover:bg-green-600 transition duration-300"
                >
                  Approve
                </button>
                <button
                  onClick={() => handleStatusUpdate(outpass._id, 'rejected')}
                  className="bg-red-500 text-white px-4 py-2 rounded-md shadow hover:bg-red-600 transition duration-300"
                >
                  Reject
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WardenDashboard;
